        return [
            'Project Management Fundamentals' => [
                // Original 3 questions
                $this->q('What is a project?', [$this->correct('A temporary endeavor to create a unique product, service, or result'), $this->wrong('Ongoing operations'), $this->wrong('Routine maintenance'), $this->wrong('Daily business activities')], 'Projects are temporary with defined beginnings and ends, creating unique deliverables, unlike ongoing operations.', 'easy', 'approved'),
                $this->q('What are the five process groups in project management?', [$this->correct('Initiating, Planning, Executing, Monitoring & Controlling, Closing'), $this->wrong('Start, Middle, End, Review, Close'), $this->wrong('Plan, Build, Test, Deploy, Maintain'), $this->wrong('Design, Develop, Deliver, Document, Done')], 'The five process groups provide a framework for managing projects from start to finish.', 'easy', 'approved'),
                $this->q('What is the project charter?', [$this->correct('A document that formally authorizes a project'), $this->wrong('The project schedule'), $this->wrong('The project budget'), $this->wrong('The risk register')], 'The project charter formally authorizes the project, names the project manager, and provides high-level requirements and objectives.', 'medium', 'approved'),
                
                // 40 new questions
                $this->q('Who is responsible for approving the project charter?', [$this->correct('The project sponsor'), $this->wrong('The project manager'), $this->wrong('The project team'), $this->wrong('The stakeholders')], 'The project sponsor, who provides financial resources and support, approves the project charter and formally authorizes the project.', 'easy', 'approved'),
                $this->q('What is the primary purpose of the project management plan?', [$this->correct('To integrate and coordinate all subsidiary plans'), $this->wrong('To list all project tasks'), $this->wrong('To track project budget'), $this->wrong('To assign team members')], 'The project management plan is a comprehensive document that integrates all subsidiary plans and baselines to guide project execution and control.', 'medium', 'approved'),
                $this->q('What is a program?', [$this->correct('A group of related projects managed in a coordinated way'), $this->wrong('A single large project'), $this->wrong('A collection of unrelated projects'), $this->wrong('An ongoing operation')], 'Programs consist of related projects, subsidiary programs, and program activities managed together to obtain benefits not available from managing them individually.', 'medium', 'approved'),
                $this->q('What is a portfolio?', [$this->correct('A collection of projects, programs, and operations managed to achieve strategic objectives'), $this->wrong('A single project'), $this->wrong('Only programs'), $this->wrong('Only operations')], 'Portfolios are collections of projects, programs, subsidiary portfolios, and operations managed as a group to achieve strategic objectives.', 'medium', 'approved'),
                $this->q('What is organizational project management (OPM)?', [$this->correct('A framework for aligning projects with organizational strategy'), $this->wrong('Managing a single project'), $this->wrong('Team management'), $this->wrong('Budget management')], 'OPM is a strategy execution framework utilizing project, program, and portfolio management to achieve organizational goals.', 'medium', 'approved'),
                $this->q('What is a project management office (PMO)?', [$this->correct('An organizational structure that standardizes project governance'), $this->wrong('The project manager\'s office'), $this->wrong('A physical location'), $this->wrong('A software tool')], 'A PMO is an organizational structure that standardizes project-related governance processes and facilitates sharing of resources, methodologies, tools, and techniques.', 'medium', 'approved'),
                $this->q('What are the three types of PMOs?', [$this->correct('Supportive, Controlling, and Directive'), $this->wrong('Active, Passive, and Neutral'), $this->wrong('Strategic, Tactical, and Operational'), $this->wrong('Internal, External, and Hybrid')], 'Supportive PMOs provide templates and best practices, Controlling PMOs require compliance, and Directive PMOs directly manage projects.', 'hard', 'approved'),
                $this->q('What is a project lifecycle?', [$this->correct('The series of phases a project passes through from initiation to closure'), $this->wrong('The project schedule'), $this->wrong('The product lifecycle'), $this->wrong('The team\'s work hours')], 'A project lifecycle defines the beginning and end of a project and what work should be done in each phase.', 'easy', 'approved'),
                $this->q('What is the difference between a phase and a process group?', [$this->correct('Phases are project-specific divisions; process groups are universal management activities'), $this->wrong('They are the same thing'), $this->wrong('Phases are shorter than process groups'), $this->wrong('Process groups only apply to agile')], 'Phases divide the project lifecycle into logical segments, while process groups (Initiating, Planning, Executing, Monitoring & Controlling, Closing) are performed throughout the project.', 'hard', 'approved'),
                $this->q('What is a deliverable?', [$this->correct('Any unique and verifiable product, result, or capability'), $this->wrong('Only the final product'), $this->wrong('Only documents'), $this->wrong('Only software code')], 'Deliverables can be tangible or intangible outputs produced during project execution, including interim and final products.', 'easy', 'approved'),
                $this->q('What is a milestone?', [$this->correct('A significant point or event in the project with zero duration'), $this->wrong('A task that takes one day'), $this->wrong('The longest task'), $this->wrong('The most expensive task')], 'Milestones mark significant achievements or decision points in the project and have no duration or resources assigned.', 'easy', 'approved'),
                $this->q('What is progressive elaboration?', [$this->correct('Continuously improving and detailing a plan as more information becomes available'), $this->wrong('Scope creep'), $this->wrong('Adding more features'), $this->wrong('Reducing scope')], 'Progressive elaboration involves iteratively developing the project management plan and project documents in greater detail as the project progresses.', 'medium', 'approved'),
                $this->q('What is the difference between a constraint and an assumption?', [$this->correct('Constraints are limiting factors; assumptions are factors believed to be true'), $this->wrong('They are the same'), $this->wrong('Constraints are always bad'), $this->wrong('Assumptions are always documented')], 'Constraints limit options (budget, schedule, resources), while assumptions are factors considered true for planning purposes but carry risk if proven false.', 'medium', 'approved'),
                $this->q('What is a project baseline?', [$this->correct('An approved version of a work product used for comparison'), $this->wrong('The first draft'), $this->wrong('The final version'), $this->wrong('The current version')], 'Baselines (scope, schedule, cost) are approved versions that can only be changed through formal change control procedures.', 'medium', 'approved'),
                $this->q('What is the triple constraint?', [$this->correct('Scope, schedule, and cost'), $this->wrong('Quality, risk, and resources'), $this->wrong('Time, money, and people'), $this->wrong('Planning, executing, and controlling')], 'The triple constraint represents the balance between scope, schedule, and cost. Changes to one typically affect the others.', 'easy', 'approved'),
                $this->q('What is a project success criterion?', [$this->correct('Measurable standards used to judge project outcomes'), $this->wrong('The project budget'), $this->wrong('The project schedule'), $this->wrong('Team satisfaction')], 'Success criteria are specific, measurable standards established during project initiation to evaluate whether the project achieved its objectives.', 'medium', 'approved'),
                $this->q('What is a business case?', [$this->correct('A documented economic feasibility study justifying the project'), $this->wrong('The project charter'), $this->wrong('The project plan'), $this->wrong('The budget')], 'The business case provides justification for the project from a business perspective, including cost-benefit analysis and return on investment.', 'medium', 'approved'),
                $this->q('What is a benefits management plan?', [$this->correct('A document describing how and when project benefits will be delivered'), $this->wrong('The project budget'), $this->wrong('The resource plan'), $this->wrong('The communication plan')], 'The benefits management plan defines what benefits the project will deliver, when they will be realized, and how they will be measured.', 'medium', 'approved'),
                $this->q('What is organizational process assets (OPA)?', [$this->correct('Plans, processes, policies, procedures, and knowledge bases specific to the organization'), $this->wrong('Physical assets'), $this->wrong('Financial assets'), $this->wrong('Human resources')], 'OPA includes any organizational artifacts, practices, or knowledge that can influence the project\'s success, such as templates, historical information, and lessons learned.', 'medium', 'approved'),
                $this->q('What are enterprise environmental factors (EEF)?', [$this->correct('Conditions not under the project team\'s control that influence the project'), $this->wrong('The project team'), $this->wrong('Project deliverables'), $this->wrong('Project documents')], 'EEF includes organizational culture, infrastructure, resource availability, market conditions, and regulations that can constrain or direct the project.', 'medium', 'approved'),
