                $this->q('What is requirements gathering?', [$this->correct('The process of eliciting, documenting, and managing stakeholder needs'), $this->wrong('Only interviewing stakeholders'), $this->wrong('Only writing documents'), $this->wrong('Only reviewing existing documents')], 'Requirements gathering uses various techniques like interviews, workshops, surveys, observation, and prototyping to understand stakeholder needs.', 'easy', 'approved'),
                $this->q('What is a focus group?', [$this->correct('A facilitated session with stakeholders to gather requirements and expectations'), $this->wrong('The project team'), $this->wrong('A risk assessment meeting'), $this->wrong('A status meeting')], 'Focus groups bring together prequalified stakeholders and subject matter experts to discuss and define requirements in a moderated, interactive setting.', 'easy', 'approved'),
